import React from "react";
import Link from 'next/link'
import PostTagItems from './post-tag-items'
import Markdown from 'react-markdown'
import text from './text'
import ProfileItem from './ProfileItem'
class PostMain extends React.Component {
  render() {
    return (
      <div>
        <div
          style={{
            width: "100%",
            height: "500px",
            backgroundPosition: 'center',
            backgroundSize: 'cover',
            textAlign: 'left',
            marginTop: '80px',
            borderTopRightRadius: "5px",
            borderTopLeftRadius: "5px",
            backgroundImage: `url(https://picsum.photos/id/0/5616/3744)`
          }}>
          </div>
          
          <div style={{marginLeft: '20px', marginRight: '20px'}}>
          <h1 style={{fontSize: 'calc(1.85vw + 25px)'}}>The Dos and Don’ts for Passing Your Technical Interview</h1>
          
          <Link href='/'>
          <div style={{display:'flex', flexDirection: 'row', alignItems: 'center'}}>
          <img style={{borderRadius: '50%'}} src='https://res.cloudinary.com/practicaldev/image/fetch/s--y4wodgwV--/c_fill,f_auto,fl_progressive,h_320,q_auto,w_320/https://thepracticaldev.s3.amazonaws.com/uploads/user/profile_image/177966/0f8eb580-5b46-4ba7-8390-18d85b04c7be.jpg' alt="text" width='40' height='40' />
          <p style={{marginLeft: '10px'}}>echoeyecodes  ・ <span>Aug 15</span> <span style={{fontWeight: 'normal'}}>  ・ 9 mins read</span></p>
          </div>
          </Link>

          <div style={{marginTop: '20px'}}>
          <PostTagItems tag='javascript' color='#2A2566'/>
          <PostTagItems tag='scala' color='#311a99'/>
          <PostTagItems tag='go-lang' color='#6c00b9'/>
          </div>

          <main id='source'>
            <Markdown className="markdown" source={text}
             />
          </main>
          </div>

          <style jsx global>{`
          .markdown p{
              font-size: 21px;
              line-height: 32px
          }
          .markdown img{
              text-align: center;
          }
          `}</style>

          <ProfileItem />


          <br />
      </div>
    );
  }
}

export default PostMain;
