import React from "react";
import Link from 'next/link'

class SignUp extends React.Component {
  ImageUpload = (evt) =>{
    var files = evt.target.files; // FileList object

      var reader = new FileReader();

      // Closure to capture the file information.
      reader.onload = (function(theFile) {
        return function(e) {
         document.getElementById('text').src=e.target.result
        };
      })(files[0]);

      // Read in the image file as a data URL.
      reader.readAsDataURL(files[0]);
    }
  render() {
    const style = {
      display: "flex",
      flexDirection: "column",
      justifyContent: "center",
      margin: "50px auto",
      height: "100%",
      width: "50%"
    };
    return (
      <div style={style}>
        <form style={style}>
          <img
          id='test'
            style={{ borderRadius: "50%", margin: "10px auto" }}
            src="static/image.jpg"
            alt="profilepicture"
            width="80"
            height="80"
          />
          <input onChange={this.ImageUpload} type='file' value=''
            style={{ width: "150px", margin: "10px auto", color: "black" }}
          />
          <br />

          <p>First Name</p>
          <input
            style={{
              border: "1px #22303f solid",
              borderRadius: "3px",
              color: "black",
              height: "20px",
              padding: "5px"
            }}
            type="text"
            placeholder="First Name"
            name="firstname"
          />

          <p>Last Name</p>
          <input
            style={{
              border: "1px #22303f solid",
              borderRadius: "3px",
              color: "black",
              height: "20px",
              padding: "5px"
            }}
            type="text"
            placeholder="Last Name"
            name="lastname"
          />

          <p>Username</p>
          <input
            style={{
              border: "1px #22303f solid",
              borderRadius: "3px",
              color: "black",
              height: "20px",
              padding: "5px"
            }}
            type="text"
            placeholder="Username"
            name="username"
          />

          <p>Email</p>
          <input
            style={{
              border: "1px #22303f solid",
              borderRadius: "3px",
              color: "black",
              height: "20px",
              padding: "5px"
            }}
            type="text"
            placeholder="Email"
            name="email"
          />

          <p>Password</p>
          <input
            style={{
              border: "1px #22303f solid",
              borderRadius: "3px",
              color: "black",
              height: "20px",
              padding: "5px"
            }}
            type="text"
            placeholder="Password"
            name="password"
          />

          <p>Confirm Password</p>
          <input
            style={{
              border: "1px #22303f solid",
              borderRadius: "3px",
              color: "black",
              height: "20px",
              padding: "5px"
            }}
            type="text"
            placeholder="Confirm Password"
            name="password1"
          />

          <button
            style={{
                width: '118px',
                color: 'white',
                borderRadius: '3px',
                fontWeight: 'bold',
                cursor: 'pointer',
                border: '2px solid white',
                padding: '5px',
                margin: '50px auto',
                backgroundColor: '#00af81'
            }}
          >
            Sign Up
          </button>

          <p style={{margin: '10px auto'}}>Already have an account? <span><Link href='/login'>Login</Link></span></p>
        </form>
      </div>
    );
  }
}

export default SignUp;
