import React from "react";

const style = {
  display: "flex",
  flexDirection: "row",
  alignItems: "center",
  marginTop: "80px",
  borderRadius: "5px",
  border: "1px #22303f solid",
  backgroundColor: "#141f2d",
  padding: "5px"
};

const ProfileItem = () => (
  <div style={style}>
    <img
      style={{ borderRadius: "5px", margin: "0px 10px" }}
      width="70px"
      height="70px"
      src="https://res.cloudinary.com/practicaldev/image/fetch/s--y4wodgwV--/c_fill,f_auto,fl_progressive,h_320,q_auto,w_320/https://thepracticaldev.s3.amazonaws.com/uploads/user/profile_image/177966/0f8eb580-5b46-4ba7-8390-18d85b04c7be.jpg"
      alt="dp"
    />
    <div id="hero" style={{ margin: "0px 10px" }}>
      <h3>EchoEye <span><button style={{
          backgroundColor: '#66e2d5',
          border: 'none',
          borderRadius: '3px',
          padding: '5px',
          color: '#000000',
          fontWeight: 'bold'
      }}>+ FOLLOW</button></span></h3>
      <p>Data Engineer | Consultant | Data Scientist</p>
      <p>@echoeyecodes</p>
    </div>
  </div>
);

export default ProfileItem;
