import React from 'react'
import Link from 'next/link'
import TagField from './tag-field'
class CreateComponent extends React.Component{
      constructor(props){
        super(props)

        this.state={
          tags:[]
        }
      }
  createTagField = (evt) =>{
      evt.preventDefault()
      const {tags} = this.state
      const obj={
        tag: ''
      }
      tags.push(obj)
      this.setState({tags})

  }
    render(){
        const style = {
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            margin: "50px auto",
            height: "100%",
            width: "80%"
          };
        return(
            <div style={style}>
        <form style={style}>

          <p>Title</p>
          <input
            style={{
              border: "1px #22303f solid",
              borderRadius: "3px",
              color: "black",
              height: "20px",
              padding: "5px"
            }}
            type="text"
            placeholder="Title"
            name="title"
          />

          <p>Content</p>
          <textarea
            style={{
              border: "1px #22303f solid",
              borderRadius: "3px",
              color: "black",
              height: "20px",
              minHeight: '100px',
              padding: "5px"
            }}
            type="text"
            placeholder="Content (in Markdown)"
            name="content"
          />

          <div style={{marginTop: '20px'}}>
            Tags:
            {this.state.tags.map(item =>  <TagField />)}
          </div>

          <button onClick={this.createTagField} style={{
             width: '118px',
             color: 'white',
             borderRadius: '3px',
             fontWeight: 'bold',
             cursor: 'pointer',
             border: '2px solid white',
             padding: '5px',
             margin: '10px',
             backgroundColor: '#00af81'
          }}>Add Tag</button>

          <button
            style={{
                width: '118px',
                color: 'white',
                borderRadius: '3px',
                fontWeight: 'bold',
                cursor: 'pointer',
                border: '2px solid white',
                padding: '5px',
                margin: '50px auto',
                backgroundColor: '#00af81'
            }}
          >
            POST
          </button>

        </form>
      </div>
        )
    }
}

export default CreateComponent
